#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\ecommerce\node_modules\.pnpm\knip@5.63.1_@types+node@24.3.1_typescript@5.9.2\node_modules\knip\bin\node_modules;C:\ecommerce\node_modules\.pnpm\knip@5.63.1_@types+node@24.3.1_typescript@5.9.2\node_modules\knip\node_modules;C:\ecommerce\node_modules\.pnpm\knip@5.63.1_@types+node@24.3.1_typescript@5.9.2\node_modules;C:\ecommerce\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/ecommerce/node_modules/.pnpm/knip@5.63.1_@types+node@24.3.1_typescript@5.9.2/node_modules/knip/bin/node_modules:/mnt/c/ecommerce/node_modules/.pnpm/knip@5.63.1_@types+node@24.3.1_typescript@5.9.2/node_modules/knip/node_modules:/mnt/c/ecommerce/node_modules/.pnpm/knip@5.63.1_@types+node@24.3.1_typescript@5.9.2/node_modules:/mnt/c/ecommerce/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/bun$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/bun$exe"  "$basedir/../knip/bin/knip-bun.js" $args
  } else {
    & "$basedir/bun$exe"  "$basedir/../knip/bin/knip-bun.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "bun$exe"  "$basedir/../knip/bin/knip-bun.js" $args
  } else {
    & "bun$exe"  "$basedir/../knip/bin/knip-bun.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
